sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new JenkinsUtils().trimStringInputs(inputs);
    outputs.username = inputs.username;
    outputs.scope = inputs.scope;
    outputs.id = inputs.credential_id ? inputs.credential_id : undefined;
    outputs.description = inputs.description ? inputs.description :
        undefined;
})(inputs, outputs);

