sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code_for_validate_id != '200' || inputs
        .status_code_for_validate_id == '404') {

        if (inputs.status_code_for_validate_id == '404') {
            if (inputs.status_code == '200') {
                outputs.status = "Success";
            } else if (inputs.status_code == '500') {
            outputs = new JenkinsErrorHandler()
                .createJenkinsCredentialsErrorHandler(inputs,
                    outputs);
        } else {
                outputs = new JenkinsErrorHandler().genericErrorHandler(
                    inputs, outputs);
            }
        }  else if (inputs.status_code_for_validate_id != '200') {
            inputs.status_code = inputs.status_code_for_validate_id;
            inputs.response = inputs.response_for_validate_id;
            outputs = new JenkinsErrorHandler().genericErrorHandler(inputs,
                outputs);
        }
    } else if (inputs.status_code_for_validate_id == '200') {
        inputs.status_code = inputs.status_code_for_validate_id;
        outputs = new JenkinsErrorHandler().genericErrorHandler(inputs,
            outputs);
    }
})(inputs, outputs);

