sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs.status_code == '201'){
    var headers = JSON.parse(inputs.header);
    var location = headers.Location[0];
    var res =  location.split("/queue/item/");
    var queue = res[1];
    var queue_id = queue.split("/");
    outputs.queue_id = queue_id[0];    
    outputs.status = "Success";
  }else{
    outputs = new JenkinsErrorHandler().jobErrorHandler(inputs,outputs);
  }
})(inputs, outputs);

