sys_variable_value.value:
(function execute(inputs, outputs) {
  //remove whitespace from input strings
   inputs = new JenkinsUtils().trimStringInputs(inputs);

    //Remove first and last slash, if present in job name
  	inputs.job_path = inputs.job_path.replace(/^\/|\/$/g, '');
  	var jobPath = "";
  	//If job has parent job as well then form a resource path with leading job for every job otherwise pass the job name as it is.  
  	if(inputs.job_path.contains("/")){
    	var parent_jobs =  inputs.job_path.split('/');	
      	for(var i=0; i<parent_jobs.length;i++ ){
        	jobPath += "/job/" + parent_jobs[i];
        }
     	outputs.job_path = jobPath.toString();
    }else{
      	outputs.job_path = "/job/" + inputs.job_path.toString();
    }

  	//Froming Name-Value pairs for Job parameters
	if(inputs.job_parameters){
      var result = [];
      inputs.job_parameters.split(',').forEach(function(x){
          var result1 = {}
          var arr = x.split('=');
           result1["name"] = arr[0];
           result1["value"] = arr[1];
          result.push(result1)          
      });
      var payload = {"parameter":result}      
      var job_param = "json="+JSON.stringify(payload);
      outputs.job_parameters = job_param.toString();

    }

})(inputs, outputs);

