sys_variable_value.value:
(function execute(inputs, outputs) {
    inputs = new JenkinsUtils().trimStringInputs(inputs);
    outputs.folder_name = inputs.folder_name;
    outputs.mode = "com.cloudbees.hudson.plugins.folder.Folder";
    var parentPath = "";
  try{
    if (inputs.parent_folder.length != 0) {
        inputs.parent_folder = inputs.parent_folder.replace(/^\/|\/$/g, '');

        var parents = inputs.parent_folder.split('/');

        for (var i = 0; i < parents.length; i++) {
            if (!parentPath) {
                parentPath = "/job/" + parents[i];
            } else {
                parentPath += "/job/" + parents[i];
            }
        }
    } 
    outputs.parent_folder = parentPath;
  }catch(e){
  	new JenkinsUtils().throwError('create_folder');
  }

})(inputs, outputs);

