sys_variable_value.value:
(function execute(inputs, outputs) {

    try {
        //remove whitespace from input strings
        inputs = new JenkinsUtils().trimStringInputs(inputs);

        outputs.job_path = inputs.job_path;

        //Remove first and last slash, if present in view path
        inputs.view_path = inputs.view_path.replace(/^\/|\/$/g, '');

        //Remove first and last slash, if present in job path
        inputs.job_path = inputs.job_path.replace(/^\/|\/$/g, '');

        //separate view name and view path
        var view_name = "";
        var view_path = "";
        if (inputs.view_path.includes("/")) {
            view_name = inputs.view_path.split("/").pop();
            var target_folder = "";
            target_folder = inputs.view_path.substring(0, inputs.view_path.lastIndexOf('/'));
            if (target_folder.includes("/")) {
                var job_path = "";
                var parent_jobs = target_folder.split('/');
                for (var i = 0; i < parent_jobs.length; i++) {
                    job_path += "/job/" + parent_jobs[i];
                }
                view_path = job_path.toString();
            } else {
                view_path = "/job/" + target_folder;
                view_path = view_path.toString()
            }
        } else
            view_name = inputs.view_path;

        outputs.view_path = view_path + "/view/" + view_name
        outputs.view_path = outputs.view_path.toString()

    } catch (err) {
        new JenkinsUtils().throwError('add_job_to_view');
    }
})(inputs, outputs);

