sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '200') {
        var response = JSON.parse(inputs.response);
        outputs.status = "Success";

        var timestamp = JSON.stringify(response.timestamp);
        timestamp = timestamp / 1000;
        var gdate = new Date(timestamp * 1000).toUTCString().replace('GMT', '');
        var date = new Date(gdate);
        var hour = date.getHours();
        if (hour >= 12) {
            if (hour != 12) {
                hour = hour - 12;
            }
            gdate = gdate + "PM";

        } else {
            gdate = gdate + "AM";
        }
        outputs.build_timestamp = gdate.toString();
        outputs.job_type = JSON.parse(inputs.response)._class;
        var build_status = JSON.stringify(response.result);
        if (build_status == "null") {
            outputs.build_status = "RUNNING";
        } else {
            outputs.build_status = build_status.replace(/"/g, "");
        }
        outputs.build_information = JSON.stringify(response);

    } else {
        outputs = new JenkinsErrorHandler().jobErrorHandler(inputs, outputs);
    }
})(inputs, outputs);

