sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '200') {
        outputs.status = "Success";
    } else {
        outputs = new JenkinsErrorHandler().viewErrorHandler(inputs,
            outputs);
    }
})(inputs, outputs);

