sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        inputs = new JenkinsUtils().trimStringInputs(inputs);
        outputs.depth = inputs.depth;
        inputs.folder_path = inputs.folder_path.replace(/^\/|\/$/g, '');
        var folder_full_path = "";
        if (inputs.folder_path.contains("/")) {
            var parent_folder = inputs.folder_path.split('/');
            for (var i = 0; i < parent_folder.length; i++) {
                folder_full_path += "/job/" + parent_folder[i];
            }
            outputs.folder_path = folder_full_path.toString();
        } else {
            outputs.folder_path = "/job/" + inputs.folder_path.toString();
        }

        var result = "jobs[name";
        var square = "]";

        for (var i = 1; i < inputs.depth; i++) {

            result = result + "," + "jobs[name";
            square = square + "]"
        }

        outputs.tree = result + square;
        outputs.tree.toString();
    } catch (err) {
      	new JenkinsUtils().throwError('get_all_jobs_from_view');
    }
})(inputs, outputs);

