sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == '200') {
        outputs.status = "Success";
        var response = JSON.parse(inputs.response);

        if (response._class == "com.cloudbees.hudson.plugins.folder.Folder")
            outputs.job_list = JSON.stringify(response.jobs);
        else
            outputs = new JenkinsErrorHandler().folderErrorHandler(inputs, outputs);

    } else {
        outputs = new JenkinsErrorHandler().folderErrorHandler(inputs, outputs);
    }
})(inputs, outputs);

