sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == '200') {
    inputs = new JenkinsUtils().trimStringInputs(inputs);
    outputs.status = "Success";

    var response = JSON.parse(inputs.response);
    var myjobs = {};
    myjobs.job = [];

     for (var key in response.jobs ){
       if(response.jobs[key]._class != 'com.cloudbees.hudson.plugins.folder.Folder'){
         myjobs.job.push({
         	class  : response.jobs[key]._class,
           name : response.jobs[key].name
         });
       }
     }

    outputs.job_lists = myjobs.job;
    outputs.job_type = JSON.parse(inputs.response)._class;
  } else {
    outputs = new JenkinsErrorHandler().viewErrorHandler(inputs, outputs);
  }
})(inputs, outputs);

