sys_variable_value.value:
(function execute(inputs, outputs) {

    try {
        //remove whitespace from input strings
        inputs = new JenkinsUtils().trimStringInputs(inputs);

        if (inputs.view_name.trim()) {
			outputs.view_name = inputs.view_name;

            //remove first and last slash 
            inputs.parent_folder = inputs.parent_folder.replace(/^\/|\/$/g, '');

            //create parent folder path 
            if (inputs.parent_folder) {
                var parentPath = "";
                if (inputs.parent_folder.contains("/")) {
                    var parents = inputs.parent_folder.split('/');
                    for (var i = 0; i < parents.length; i++) {
                        parentPath += "/job/" + parents[i];
                    }
                    outputs.parent_folder = parentPath.toString();
                } else {
                    outputs.parent_folder = "/job/" + inputs.parent_folder;
                    outputs.parent_folder = outputs.parent_folder.toString();
                }
            }

        } else {
            new JenkinsUtils().throwError('get_all_jobs_from_view_view_not_found');
        }
    } catch (err) {
        new JenkinsUtils().throwError('get_all_jobs_from_view');
    }
})(inputs, outputs);

