sys_variable_value.value:
(function execute(inputs, outputs) {
    //remove whitespace from input strings
    inputs = new JenkinsUtils().trimStringInputs(inputs);
    outputs.restart_type = inputs.restart_type ? inputs.restart_type :
        undefined;
})(inputs, outputs);

