sys_variable_value.value:
(function execute(inputs, outputs) {
    //Check whether action is successfull or not
    if ((inputs.status_code_for_restart_jenkins == '503' && inputs
            .status_code_for_test_connection == '200') || (inputs
            .status_code_for_restart_jenkins == '503' && inputs
            .status_code_for_test_connection == '503')) {
        //On success set the status as success when Jenkins is restarting
        if (inputs.response_for_restart_jenkins.indexOf(
                'Please wait while Jenkins is getting ready to work') != -
            1 || inputs.response_for_restart_jenkins.indexOf(
                'Please wait while Jenkins is restarting') != -1) {
            outputs.status = "Success";
        } else {
            inputs.status_code = inputs.status_code_for_restart_jenkins;
            inputs.response = inputs.response_for_restart_jenkins;

            outputs = new JenkinsErrorHandler().genericErrorHandler(inputs,
                outputs);
        }
    } else {
        //Set action's errors here
        if (inputs.status_code_for_restart_jenkins != '503') {
            if (inputs.status_code_for_restart_jenkins == '200' && inputs
                .status_code_for_test_connection == '200') {
                inputs.status_code = inputs.status_code_for_restart_jenkins;
                inputs.response = "invalid_restart_type";
            } else {
                inputs.status_code = inputs.status_code_for_restart_jenkins;
                inputs.response = inputs.response_for_restart_jenkins;
            }
        } else {
            inputs.status_code = inputs.status_code_for_test_connection;
            inputs.response = inputs.response_for_test_connection;
        }
        outputs = new JenkinsErrorHandler().genericErrorHandler(inputs,
            outputs);
    }
})(inputs, outputs);

