sys_variable_value.value:
(function execute(inputs, outputs) {
    //Check whether action is successfull or not
    if (inputs.status_code == '200') {
        //On success set the status as success
        outputs.status = "Success";
        var response = JSON.parse(inputs.response);
        var timestamp = JSON.stringify(response.timestamp);
        timestamp = timestamp / 1000;
        var gdate = new Date(timestamp * 1000).toUTCString().replace('GMT',
            '');
        //Get hours from retrived date 
        var date = new Date(gdate);
        var hour = date.getHours();
        if (hour >= 12) {
            if (hour != 12) {
                hour = hour - 12;
            }
            gdate = gdate + "PM";

        } else {
            gdate = gdate + "AM";
        }

        outputs.build_timestamp = gdate.toString();

    } else {
        //Set action's errors here
        outputs = new JenkinsErrorHandler().jobErrorHandler(inputs,
        outputs);
    }
})(inputs, outputs);

